{let n=function(){let n=$("#new-post-form");n.submit((function(o){o.preventDefault(),$.ajax({type:"post",url:"/posts/create",data:n.serialize(),success:function(n){console.log(n);let o=new t(n.data.post);$("#posts-list-container>ul").prepend(o),e($(" .delete-post-button",o))},error:function(n){console.log(n.responseText)}})}))},t=function(n){return $(`<li id="post-${n._id}">\n        <p>\n            \n            <small>\n                \n                <a class="delete-post-button" href="/posts/destroy/${n._id}">x</a>\n            </small>\n            \n            <br>\n            ${n.content}\n            <br>\n            <small>\n                ${n.user.name}\n            </small> \n        </p>\n    \n        <div class="post-comments">\n            \n                <form action="/comments/create" method="POST">\n                    <input type="text" name="content" placeholder="Add comments">\n                    <input type="hidden" name="post" value="${n._id}">\n                    <input type="submit" value="Add comment">\n                </form>\n    \n    \n    \n            \n    \n            <div class="post-comments-list"></div>\n                <ul id="post-comments-${n._id}"></ul>\n    \n            </div>\n        \n    </li>`)},e=function(n){$(n).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(n).prop("href"),success:function(n){$(`#post-${n.data.post_id}`).remove()},error:function(n){console.log(n.responseText)}})}))};n()}